DROP TABLE IF EXISTS `view_all_authors`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `view_all_authors` AS select `a`.`author_id` AS `author_id`,`a`.`author_name` AS `author_name`,`a`.`author_desc` AS `author_desc`,`a`.`author_picture` AS `author_picture`,`a`.`author_active` AS `author_active`,count(`q`.`paperId`) AS `paper_nbr` from ((`author` `a` left join `paperauthor` `q` on(((`q`.`authorId` = `a`.`author_id`) and (`q`.`paperauthor_active` = 1)))) join `paper` `p` on(((`q`.`paperId` = `p`.`id`) and (`p`.`paper_active` = 1)))) where (`a`.`author_active` = 1) group by `a`.`author_id`;



DROP TABLE IF EXISTS `view_authors_class`;

