("11","Gerostathopoulos2016","Self-adaptation in software-intensive cyber\\endashphysical systems: From system goals to architecture configurations","<p><b><i>ARTICLE</i>(Gerostathopoulos2016)</b></p>\n<p>I. Gerostathopoulos., T. Bures., P. Hnetynka., J. Keznikl., M. Kit., F. Plasil., and N. Plouzeau. Self-adaptation in software-intensive cyber\\endashphysical systems: From system goals to architecture configurations. <i>Journal of Systems and Software</i>: 122, pp. 378&#8211;397 (2016).</p>\n<p><center></center></p>","@ARTICLE{Gerostathopoulos2016,\n  author = {Gerostathopoulos, Ilias and Bures, Tomas and Hnetynka, Petr and Keznikl, Jaroslav and Kit, Michal and Plasil, Frantisek and Plouzeau, No{\\\"{e}}l},\n  journal = {Journal of Systems and Software},\n  title = {{Self-adaptation in software-intensive cyber\\endashphysical systems: From system goals to architecture configurations}},\n  year = {2016},\n  abstract = {Design of self-adaptive software-intensive cyber\\endashphysical systems (siCPS) operating in dynamic environments is a significant challenge when a sufficient level of dependability is required. This stems partly from the fact that the concerns of self-adaptivity and dependability are to an extent contradictory. In this paper, we introduce IRM-SA (Invariant Refinement Method for Self-Adaptation)\\emdasha design method and associated formally grounded model targeting siCPS\\emdashthat addresses self-adaptivity and supports dependability by providing traceability between system requirements, distinct situations in the environment, and predefined configurations of system architecture. Additionally, IRM-SA allows for architecture self-adaptation at runtime and integrates the mechanism of predictive monitoring that deals with operational uncertainty. As a proof of concept, it was implemented in DEECo, a component framework that is based on dynamic ensembles of components. Furthermore, its feasibility was evaluated in experimental settings assuming decentralized system operation.},\n  pages = {378--397},\n  volume = {122},\n  doi = {https://doi.org/10.1016/j.jss.2016.02.028}\n}","Design of self-adaptive software-intensive cyber\\endashphysical systems (siCPS) operating in dynamic environments is a significant challenge when a sufficient level of dependability is required. This stems partly from the fact that the concerns of self-adaptivity and dependability are to an extent contradictory. In this paper, we introduce IRM-SA (Invariant Refinement Method for Self-Adaptation)\\emdasha design method and associated formally grounded model targeting siCPS\\emdashthat addresses self-adaptivity and supports dependability by providing traceability between system requirements, distinct situations in the environment, and predefined configurations of system architecture. Additionally, IRM-SA allows for architecture self-adaptation at runtime and integrates the mechanism of predictive monitoring that deals with operational uncertainty. As a proof of concept, it was implemented in DEECo, a component framework that is based on dynamic ensembles of components. Furthermore, its feasibility was evaluated in experimental settings assuming decentralized system operation.","","2016","9",NULL,NULL,"17","2018-09-29 15:46:50","Manually","","Pending","Waiting","0","17_1538250409","3"),
("12","Gribaudo2018","A performance modeling framework for lambda architecture based applications","<p><b><i>ARTICLE</i>(Gribaudo2018)</b></p>\n<p>M. Gribaudo., M. Iacono., and M. Kiran. A performance modeling framework for lambda architecture based applications. <i>Future Generation Computer Systems</i>: 86, pp. 1032&#8211;1041 (2018).</p>\n<p><center></center></p>","@ARTICLE{Gribaudo2018,\n  author = {Gribaudo, M and Iacono, M and Kiran, M},\n  journal = {Future Generation Computer Systems},\n  title = {{A performance modeling framework for lambda architecture based applications}},\n  year = {2018},\n  abstract = {The lambda architectural pattern allows to overcome some limitations of data processing frameworks. It builds on the methodology of having two different data processing streams on the same system: a real time computing for fast data streams and a batch computing behavior for massive workloads for delayed processing. While these two modes are clearly not new, lambda architectures allow them to coordinate their execution to avoid interference. However resource allocation over cloud infrastructure, has greatly impacted the overall performances (and importantly costs). If performance could be modeled in advance, architects could make better judgments on allocation of their resources to use the systems more efficiently. In this paper, we present a modeling approach, based on multiformalism and multisolution techniques, that provides a fast evaluation tool to support design choices about parameters and eventually lead to better architecture designs.},\n  pages = {1032--1041},\n  volume = {86},\n  doi = {https://doi.org/10.1016/j.future.2017.07.033}\n}","The lambda architectural pattern allows to overcome some limitations of data processing frameworks. It builds on the methodology of having two different data processing streams on the same system: a real time computing for fast data streams and a batch computing behavior for massive workloads for delayed processing. While these two modes are clearly not new, lambda architectures allow them to coordinate their execution to avoid interference. However resource allocation over cloud infrastructure, has greatly impacted the overall performances (and importantly costs). If performance could be modeled in advance, architects could make better judgments on allocation of their resources to use the systems more efficiently. In this paper, we present a modeling approach, based on multiformalism and multisolution techniques, that provides a fast evaluation tool to support design choices about parameters and eventually lead to better architecture designs.","","2018","6",NULL,NULL,"17","2018-09-29 15:46:50","Manually","","Pending","Waiting","0","17_1538250409","3"),
("13","Gribaudo2016","Hybrid Petri nets with general one-shot transitions","<p><b><i>ARTICLE</i>(Gribaudo2016)</b></p>\n<p>M. Gribaudo. and A. Remke. Hybrid Petri nets with general one-shot transitions. <i>Performance Evaluation</i>: 105, pp. 22&#8211;50 (2016).</p>\n<p><center></center></p>","@ARTICLE{Gribaudo2016,\n  author = {Gribaudo, Marco and Remke, Anne},\n  journal = {Performance Evaluation},\n  title = {{Hybrid Petri nets with general one-shot transitions}},\n  year = {2016},\n  abstract = {A hybrid Petri net formalism that allows deterministic, and fluid transitions is extended by generally distributed transitions that moves discrete tokens. Models in this formalism can be analyzed with Parametric Reachability Analysis, by computing all reachable locations, and by separating the deterministic and the stochastic evolution of the system. Several performance metrics, such as the distribution of fluid over time, can be derived by deconditioning according to arbitrary continuous probability distributions. This efficient concept allows for the analysis of an arbitrary number of fluid places, as opposed to classical stochastic hybrid Petri net approaches. Moreover, validation of our results against a FSPN tool shows that parametric reachability analysis provides more accurate results. A case study motivates and shows the potential of our approach.},\n  pages = {22--50},\n  volume = {105},\n  doi = {https://doi.org/10.1016/j.peva.2016.09.002}\n}","A hybrid Petri net formalism that allows deterministic, and fluid transitions is extended by generally distributed transitions that moves discrete tokens. Models in this formalism can be analyzed with Parametric Reachability Analysis, by computing all reachable locations, and by separating the deterministic and the stochastic evolution of the system. Several performance metrics, such as the distribution of fluid over time, can be derived by deconditioning according to arbitrary continuous probability distributions. This 